const cart =[];

const handleCart =(state = cart, action) =>{
    const product = action.payload; 
    switch (action.type) {
        case "ADDITEM" : return [
            ...state,
            product
        ]

        case "DELITEM" :
            return state = state.filter((x)=>{
                return x.id !==product
            })
        default:
            return state;
    }
}
export default handleCart;
