const cart =[];

const handleCart =(state = cart, action) =>{
    const product = action.payload; 
    switch (action.type) {
        case "ADDITEM" : return [
            ...state,
            action.payload
        ]

            case "DELITEM":
                const exist1 = state.find((x)=>x.id===product.id);
                if(exist1.qty ===1){
                    return state.filter((x)=>x.id !==exist1.id);
                }else
                {
                    return state.map((x)=>
                    x.id === product.id ? {...x,qty: x.qty - 1} :x );
                }    
        default:
            return state;
    }
}
export default handleCart;
